import React,{ Component } from 'react';
import Popup from "reactjs-popup";
import Content from "./Content.js";


const axios = require ('axios');


class App extends Component {
    constructor(props) {
        super(props);
        this.state = {
            studentname: '',
            phonenumber: '',
        };
        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
        this.axiosPostData = this.axiosPostData.bind(this);
        this.axiosGetData = this.axiosGetData.bind(this);
    }
    /*
    mySubmitHandler = (event) => {
        event.preventDefault();
        alert("You are submitting " + this.state.studentname);
    }
    myChangeHandler = (event) => {
        //this.setState({username: event.target.value});
        let nam = event.target.name;
        let val = event.target.value;
        this.setState({[nam]: val});
    }
    */


    axiosGetData = (event) => {
        //  event.preventDefault(); is used to prevent frontend to refresh itself automatically when changes are made
        event.preventDefault();
        axios.get('http://localhost:5000/watch',{

        })
            .then((response) => {
                //const path = '/result';
                //browserHistory.push(path);
                console.log("doing stuff hree");
                console.log(response.data);
            })
            .catch(function (error) {
                console.log(error);
            });

    }
    axiosPostData = (event) => {
        //  event.preventDefault(); is used to prevent frontend to refresh itself automatically when changes are made
        event.preventDefault();
        //this.setState({[event.target.name]:event.target.value});

        axios.post('http://localhost:5000/test', {
            studentname: this.state.studentname,
            phonenumber: this.state.phonenumber
        })
            .then(function (response) {
                console.log(response);
                alert('A name was submitted: ' + this.state.studentname+ 'age is'+this.state.phonenumber);
            })
            .catch(function (error) {
                console.log(error);
            });

    }


    handleChange (event) {
        //prevent may not needed?
        //event.preventDefault();
        this.setState({[event.target.name]:event.target.value});
    }


    handleSubmit(event) {
        event.preventDefault();
        // alert('A name was submitted: ' + this.state.studentname+ 'age is'+this.state.phonenumber);
        alert("yiha");
    }


    render() {
        let header = '';

        return (

            <div className="App">
                <form onSubmit={this.axiosGetData}>
                    <input type="submit" value="getData" />
                </form>
                <form onSubmit={this.axiosPostData}>
                    <label>Enter your student name:</label>
                    <input
                        type='text'
                        name='studentname'
                        value={this.state.studentname}
                        onChange={this.handleChange}
                    />
                    <label>Enter your phone number:</label>
                    <input
                        type='text'
                        name='phonenumber'
                        value={this.state.phonenumber}
                        onChange={this.handleChange}
                    />
                    <input type="submit" value="Submit" />

                </form>


                <h1>Click below to pop up </h1>
                <Popup modal trigger={<button>Click Me</button>}>
                    {close => <Content close={close} />}
                </Popup>
            </div>

        )
    }
    /*
    handleClick(){
        window.open("/MyScreen");
    }
     */


}
export default App
