import React, {Component} from 'react';
import Popup from "reactjs-popup";
import Content from "./Content.js";
import fetch from "node-fetch";
import 'whatwg-fetch';

const axios = require('axios');


class App extends Component {
    constructor(props) {
        super(props);
        this.state = {
            studentname: '',
            phonenumber: '',
            searchname: '',
            course:'',
        };
        this.handleChange = this.handleChange.bind(this);
        //this.handleSubmit = this.handleSubmit.bind(this);
        this.axiosPostData = this.axiosPostData.bind(this);
        this.fetchGetData = this.fetchGetData.bind(this);

    }

    //Used to find specific value according to users' input, result as record can only be one
    //Somehow ios cant send or retrieve info using fetch/axios
    fetchGetData = (event) => {
        event.preventDefault();
        console.log(event.target.searchname.value);


        //work
        //192.168.1.104 as localhost address
        fetch("http://192.168.1.104:5000/search/"+event.target.searchname.value)
            .then(res => res.json())
            .then(
                (res) => {
                    console.log(res+"_!!!@@!@!@");
                    alert("name is :"+res[0].studentname + "number is:"+ res[0].phonenumber);
                },
                // Note: it's important to handle errors here
                // instead of a catch() block so that we don't swallow
                // exceptions from actual bugs in components.
                (error) => {
                    this.setState({
                        isLoaded: true,
                        error
                    });
                }
            )
        //work
    };

    //This is used to find multiple records
    fetchGetData = (event) => {
        event.preventDefault();
        console.log(event.target.searchname.value);


        //work
        //192.168.1.104 as localhost address
        fetch("http://192.168.1.104:5000/search/"+event.target.searchname.value)
            .then(res => res.json())
            .then(
                (res) => {
                    console.log(res+"_!!!@@!@!@");
                    alert("name is :"+res[0].studentname + "number is:"+ res[0].phonenumber);
                },
                // Note: it's important to handle errors here
                // instead of a catch() block so that we don't swallow
                // exceptions from actual bugs in components.
                (error) => {
                    this.setState({
                        isLoaded: true,
                        error
                    });
                }
            )
        //work
    };



    axiosPostData = (event) => {
        //  event.preventDefault(); is used to prevent frontend real actions
        //  (in this case :to refresh itself automatically when changes are made)
        event.preventDefault();
        //this.setState({[event.target.name]:event.target.value});
        //console.log(this.state.searchname);
        axios.post('http://192.168.1.104:5000/test', {
            studentname: this.state.studentname,
            phonenumber: this.state.phonenumber,
        })
            .then(function (response) {
                console.log(response);

            })
            .catch(function (error) {
                console.log(error);
            });

        alert('A name was submitted: ' + this.state.studentname + '  phone number is' + this.state.phonenumber);

    }


    handleChange(event) {
        //prevent may not needed?
        //event.preventDefault();
        //value: event.target.value
        this.setState({[event.target.name]: event.target.value});

    }

    /*
        handleSubmit(event) {
            event.preventDefault();
            //alert('A name was submitted: ' + this.state.studentname+ 'age is'+this.state.phonenumber);
            alert("yiha");
        }
    */

    render() {
        let header = '';

        return (

            <div className="App">
                <form onSubmit={this.fetchGetData}>
                    <label>You want to search:</label>
                    <input
                        type='text'
                        name='searchname'
                        value={this.state.searchname}
                        onChange={this.handleChange}
                    />
                    <input type="submit" value="getData"/>

                </form>

                <form onSubmit={this.axiosPostData}>
                    <label>Enter your student name:</label>
                    <input
                        type='text'
                        name='studentname'
                        value={this.state.studentname}
                        onChange={this.handleChange}
                    />
                    <label>Enter your phone number:</label>
                    <input
                        type='text'
                        name='phonenumber'
                        value={this.state.phonenumber}
                        onChange={this.handleChange}
                    />

                    <input type="submit" value="Submit"/>

                </form>


                <h1>Click below to pop up </h1>
                <Popup modal trigger={<button>Click Me</button>}>
                    {close => <Content close={close}/>}
                </Popup>

            </div>

        )
    }

    /*
    handleClick(){
        window.open("/MyScreen");
    }
     */


}

export default App
