class App extends Component {
    constructor(props) {
        super(props);
        this.state = {
            data:"js是世界上最好的语言"
        };
    }
    render() {
        return (
            <div style={{backgroundColor:"#0ff",fontSize:"20px",color:"#00f"}} onClick={()=>this.click(this.state.data)}>
                {this.state.data}

            </div>
        )
    }

    click=()=>{
        this.setState({
            data: "已经提交,谢谢!"
