import React,{ Component } from 'react';
import ReactDOM from "react-dom";
import Popup from "reactjs-popup";
import Content from "./Content.js";


class App extends Component {
    constructor(props) {
        super(props);
        this.state = {
            username: '',
            age: null,
        };
        this.handleChange = this.handleChange.bind(this);

        this.handleSubmit = this.handleSubmit.bind(this);

    }
    mySubmitHandler = (event) => {
        event.preventDefault();
        alert("You are submitting " + this.state.username);
    }
    myChangeHandler = (event) => {
        //this.setState({username: event.target.value});
        let nam = event.target.name;
        let val = event.target.value;
        this.setState({[nam]: val});
    }
    handleChange (event) {

        this.setState({[event.target.name]:event.target.value});
    }
    handleSubmit(event) {
        alert('A name was submitted: ' + this.state.username+ 'age is'+this.state.age);
        event.preventDefault();
    }


    render() {
        let header = '';

        return (

            <div className="App">
                <form onSubmit={this.handleSubmit}>
                    <label>Enter your name:</label>
                    <input
                        type='text'
                        name='username'
                        value={this.state.username}

                        onChange={this.handleChange}
                        //onSubmit={this.handleSubmit}
                        //onChange={this.myChangeHandler}
                    />
                    <label>Enter your age:</label>
                    <input
                        type='text'
                        name='age'
                        value={this.state.age}
                        onChange={this.handleChange}
                        //onSubmit={this.handleSubmit}
                        //onChange={this.myChangeHandler}
                    />
                    <input type="submit" value="Submit" />
                </form>

                <h1>Create React Modal with 22 line of code </h1>
                <Popup modal trigger={<button>Click Me</button>}>
                    {close => <Content close={close} />}
                </Popup>
            </div>

        )
    }

    handleClick(){

        window.open("/MyScreen");
        //this opens in a new tab (believe that is what the owner of the question wanted if not you can do window.location.href = "/insert/your/path/here".
    }


}
export default App
