import React,{ Component } from 'react';
import ReactDOM from "react-dom";
import Popup from "reactjs-popup";
import Content from "./Content.js";


class App extends Component {
    constructor(props) {
        super(props);
        this.state = {
            username: '',
            age: null,
        };
        this.handleSubmit = this.handleSubmit.bind(this);

    }
    mySubmitHandler = (event) => {
        event.preventDefault();
        alert("You are submitting " + this.state.username);
    }
    myChangeHandler = (event) => {
        //this.setState({username: event.target.value});
        let nam = event.target.name;
        let val = event.target.value;
        this.setState({[nam]: val});
    }
    handleSubmit(event) {
        alert('A name was submitted: ' + this.state.username.value);
        alert('An age was submitted: ' + this.state.age.value);

        event.preventDefault();
    }
    handleChange(event) {
        this.setState({name: event.target.name});
        this.setState({age: event.target.age});
    }
    render() {
        let header = '';

        return (

            <div className="App">
                <form onSubmit={this.handleSubmit}>
                    <p>Enter your name:</p>
                    <input
                        type='text'
                        name='username'
                        onChange={this.handleChange}

                        //onChange={this.myChangeHandler}
                    />
                    <p>Enter your age:</p>
                    <input
                        type='text'
                        name='age'
                        onChange={this.handleChange}

                        //onChange={this.myChangeHandler}
                    />

                </form>

                <h1>Create React Modal with 22 line of code </h1>
                <Popup modal trigger={<button>Click Me</button>}>
                    {close => <Content close={close} />}
                </Popup>
            </div>

        )
    }

    handleClick(){

        window.open("/MyScreen");
        //this opens in a new tab (believe that is what the owner of the question wanted if not you can do window.location.href = "/insert/your/path/here".
    }


}
export default App
